punkty=0
pomylki=0

random.shuffle(wersety)
for werset in wersety:
    werset = werset.split()

    #wylosowanie hasła
    l = len((werset))
    rand = random.randrange(l-1)
    haslo= werset[rand]

     
    llen = len(haslo)
    lastchar = haslo[-1]
    if lastchar.isalpha():
        werset[rand]='_ ' * llen    #wymazanie hasła z pytania 
    else:
        haslo=haslo[:-1]    #pomijanie przecinków i kropek 
        werset[rand]=('_ ' * (llen-1)) + lastchar    #wymazanie hasła z pytania 
        llen-=1

    #interakcja z urzytkownikiem
    errors=0
    podpowiedz=""
    print(' '.join(werset)) 
    text=""
    while True:
        print("Wpisz brakujące słowo :")
        text = input()
        if text.lower() == haslo.lower():
            punkty+=1
            break
        if text.lower() == "exit":
            break
        pomylki+=1
        errors+=1
        if (errors//3 < llen): 
            if errors % 3 ==0:
                podpowiedz += haslo[(errors//3)-1]
                werset[rand]=podpowiedz + werset[rand][(((errors//3)*2)-1):]#uzupełnianie liter
                print(' '.join(werset)) 
        else:
            break

    if text.lower() == "exit":
            break

    if (errors//3 < llen):
        print("Brawo!!!")
    else:print("Źle! prawidłowe haslo to: " + haslo)


print("Twój wynik to: " + str(punkty) + ", liczba błędów: " + str(pomylki) )
